import type { Card, CardState } from '../types'

// Helper to create mock card data with all required fields
function createMockCard(overrides: Partial<Card> & { id: string; title: string }): Card {
  return {
    projectId: 'project-1',
    description: '',
    taskPrompt: 'Test task prompt',
    state: 'draft',
    loopIteration: 0,
    errorCount: 0,
    totalCostUsd: 0,
    totalTokens: 0,
    labels: [],
    priority: 0,
    createdAt: '2024-01-01T00:00:00Z',
    updatedAt: '2024-01-01T00:00:00Z',
    ...overrides,
  }
}

// Mock card data for testing
const mockCards: Card[] = [
  createMockCard({
    id: '1',
    title: 'Test Card 1',
    description: 'Test description 1',
    state: 'draft',
    labels: ['frontend'],
    projectId: 'project-1',
    createdAt: '2024-01-01T00:00:00Z',
    updatedAt: '2024-01-01T00:00:00Z',
  }),
  createMockCard({
    id: '2',
    title: 'Test Card 2',
    description: 'Test description 2',
    state: 'planning',
    labels: ['backend', 'api'],
    projectId: 'project-1',
    createdAt: '2024-01-02T00:00:00Z',
    updatedAt: '2024-01-02T00:00:00Z',
  }),
  createMockCard({
    id: '3',
    title: 'Another Card',
    description: 'Different description',
    state: 'draft',
    labels: ['testing'],
    projectId: 'project-2',
    createdAt: '2024-01-03T00:00:00Z',
    updatedAt: '2024-01-03T00:00:00Z',
  }),
]

describe('cardStore', () => {
  beforeEach(() => {
    // Reset the store before each test
    useCardStore.getState().setCards([])
    useCardStore.getState().setSelectedCard(null)
    useCardStore.getState().setFilters({
      states: [],
      labels: [],
      search: '',
      projectId: null,
    })
  })

  describe('Basic CRUD operations', () => {
    it('should set cards correctly', () => {
      const store = useCardStore.getState()
      store.setCards(mockCards)

      // Must get fresh state after mutations
      const currentState = useCardStore.getState()
      expect(currentState.cards.size).toBe(3)
      expect(currentState.getCard('1')).toEqual(mockCards[0])
      expect(currentState.getCard('2')).toEqual(mockCards[1])
      expect(currentState.getCard('3')).toEqual(mockCards[2])
    })

    it('should add a new card', () => {
      const store = useCardStore.getState()
      const newCard = createMockCard({
        id: '4',
        title: 'New Card',
        description: 'New description',
        state: 'draft',
        labels: [],
        projectId: 'project-1',
        createdAt: '2024-01-04T00:00:00Z',
        updatedAt: '2024-01-04T00:00:00Z',
      })

      store.addCard(newCard)

      // Must get fresh state after mutations
      const currentState = useCardStore.getState()
      expect(currentState.cards.size).toBe(1)
      expect(currentState.getCard('4')).toEqual(newCard)
    })

    it('should update an existing card', () => {
      const store = useCardStore.getState()
      store.setCards([mockCards[0]])

      const updatedCard = { ...mockCards[0], title: 'Updated Title' }
      store.updateCard(updatedCard)

      expect(store.getCard('1')).toEqual(updatedCard)
      expect(store.getCard('1')?.title).toBe('Updated Title')
    })

    it('should remove a card', () => {
      const store = useCardStore.getState()
      store.setCards(mockCards)

      // Get fresh state and remove a card
      let currentState = useCardStore.getState()
      currentState.removeCard('2')

      // Must get fresh state after mutations
      currentState = useCardStore.getState()
      expect(currentState.cards.size).toBe(2)
      expect(currentState.getCard('2')).toBeUndefined()
      expect(currentState.getCard('1')).toBeDefined()
      expect(currentState.getCard('3')).toBeDefined()
    })
  })

  describe('Selection management', () => {
    it('should set and get selected card', () => {
      let currentState = useCardStore.getState()

      expect(currentState.selectedCardId).toBeNull()

      currentState.setSelectedCard('1')
      // Must get fresh state after mutations
      currentState = useCardStore.getState()
      expect(currentState.selectedCardId).toBe('1')

      currentState.setSelectedCard(null)
      // Must get fresh state after mutations
      currentState = useCardStore.getState()
      expect(currentState.selectedCardId).toBeNull()
    })
  })

  describe('State-based filtering', () => {
    beforeEach(() => {
      const store = useCardStore.getState()
      store.setCards(mockCards)
    })

    it('should get cards by state', () => {
      const store = useCardStore.getState()

      const draftCards = store.getCardsByState('draft')
      expect(draftCards).toHaveLength(2)
      expect(draftCards[0].id).toBe('1')
      expect(draftCards[1].id).toBe('3')

      const planningCards = store.getCardsByState('planning')
      expect(planningCards).toHaveLength(1)
      expect(planningCards[0].id).toBe('2')
    })

    it('should filter cards by project when getting cards by state', () => {
      const store = useCardStore.getState()
      store.setFilters({ projectId: 'project-1' })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const draftCards = currentState.getCardsByState('draft')
      expect(draftCards).toHaveLength(1)
      expect(draftCards[0].id).toBe('1')
    })
  })

  describe('Advanced filtering', () => {
    beforeEach(() => {
      const store = useCardStore.getState()
      store.setCards(mockCards)
    })

    it('should filter by multiple states', () => {
      const store = useCardStore.getState()
      store.setFilters({ states: ['draft', 'planning'] })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(3)
    })

    it('should filter by project ID', () => {
      const store = useCardStore.getState()
      store.setFilters({ projectId: 'project-1' })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(2)
      expect(filtered.map(c => c.id).sort()).toEqual(['1', '2'])
    })

    it('should filter by labels', () => {
      const store = useCardStore.getState()
      store.setFilters({ labels: ['backend'] })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(1)
      expect(filtered[0].id).toBe('2')
    })

    it('should filter by search text', () => {
      const store = useCardStore.getState()
      store.setFilters({ search: 'Another' })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(1)
      expect(filtered[0].id).toBe('3')
    })

    it('should filter by search in description', () => {
      const store = useCardStore.getState()
      store.setFilters({ search: 'Different' })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(1)
      expect(filtered[0].id).toBe('3')
    })

    it('should combine multiple filters', () => {
      const store = useCardStore.getState()
      store.setFilters({
        projectId: 'project-1',
        states: ['draft'],
        labels: ['frontend'],
      })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(1)
      expect(filtered[0].id).toBe('1')
    })

    it('should return no results for impossible filter combinations', () => {
      const store = useCardStore.getState()
      store.setFilters({
        projectId: 'project-1',
        labels: ['nonexistent'],
      })

      // Get fresh state after filter mutation
      const currentState = useCardStore.getState()
      const filtered = currentState.getFilteredCards()
      expect(filtered).toHaveLength(0)
    })
  })

  describe('Loading state', () => {
    it('should manage loading state', () => {
      let currentState = useCardStore.getState()

      expect(currentState.isLoading).toBe(false)

      currentState.setLoading(true)
      // Must get fresh state after mutations
      currentState = useCardStore.getState()
      expect(currentState.isLoading).toBe(true)

      currentState.setLoading(false)
      // Must get fresh state after mutations
      currentState = useCardStore.getState()
      expect(currentState.isLoading).toBe(false)
    })
  })
})

import { describe, it, expect, beforeEach } from 'vitest'
import { useLoopStore } from './loopStore'
import type { LoopState, LoopStatus, LoopConfig } from '../types'

// Default config for mock loop states
const defaultConfig: LoopConfig = {
  maxIterations: 100,
  maxRuntimeSeconds: 3600,
  maxCostUsd: 10.00,
  checkpointInterval: 10,
  cooldownSeconds: 5,
  maxConsecutiveErrors: 3,
  completionSignal: 'TASK_COMPLETE',
}

// Helper to create mock loop state with all required fields
function createMockLoopState(overrides: Partial<LoopState> & { cardId: string }): LoopState {
  return {
    iteration: 0,
    status: 'running',
    totalCostUsd: 0,
    totalTokens: 0,
    consecutiveErrors: 0,
    startTime: '2024-01-01T10:00:00Z',
    elapsedSeconds: 0,
    config: defaultConfig,
    ...overrides,
  }
}

// Mock loop data for testing
const mockLoopState: LoopState = createMockLoopState({
  cardId: 'card-1',
  status: 'running',
  iteration: 5,
  totalCostUsd: 2.50,
  totalTokens: 15000,
  startTime: '2024-01-01T10:00:00Z',
  elapsedSeconds: 1800,
})

const mockLoopState2: LoopState = createMockLoopState({
  cardId: 'card-2',
  status: 'paused',
  iteration: 2,
  totalCostUsd: 1.25,
  totalTokens: 7500,
  startTime: '2024-01-01T09:00:00Z',
  elapsedSeconds: 900,
})

const mockLoopState3: LoopState = createMockLoopState({
  cardId: 'card-3',
  status: 'completed',
  iteration: 10,
  totalCostUsd: 5.75,
  totalTokens: 30000,
  startTime: '2024-01-01T08:00:00Z',
  elapsedSeconds: 2700,
})

describe('loopStore', () => {
  beforeEach(() => {
    // Reset the store before each test
    useLoopStore.getState().clearAllLoops()
  })

  describe('Basic loop management', () => {
    it('should set and get loop state', () => {
      const store = useLoopStore.getState()

      expect(store.getLoopState('card-1')).toBeUndefined()

      store.setLoopState('card-1', mockLoopState)

      const retrievedState = store.getLoopState('card-1')
      expect(retrievedState).toEqual(mockLoopState)
    })

    it('should remove loop state when set to null', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)

      expect(store.getLoopState('card-1')).toBeDefined()

      store.setLoopState('card-1', null)

      expect(store.getLoopState('card-1')).toBeUndefined()
    })

    it('should manage multiple loop states', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)
      store.setLoopState('card-2', mockLoopState2)

      // Must get fresh state after mutations
      const currentState = useLoopStore.getState()
      expect(currentState.getLoopState('card-1')).toEqual(mockLoopState)
      expect(currentState.getLoopState('card-2')).toEqual(mockLoopState2)
      expect(currentState.activeLoops.size).toBe(2)
    })
  })

  describe('Loop iteration updates', () => {
    it('should update loop iteration data', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)

      store.updateLoopIteration('card-1', 7, 3.50, 18000)

      const updatedState = store.getLoopState('card-1')
      expect(updatedState).toEqual({
        ...mockLoopState,
        iteration: 7,
        totalCostUsd: 3.50,
        totalTokens: 18000,
      })
    })

    it('should not update non-existent loop iteration', () => {
      const store = useLoopStore.getState()

      store.updateLoopIteration('nonexistent', 7, 3.50, 18000)

      // Must get fresh state after mutations
      const currentState = useLoopStore.getState()
      expect(currentState.getLoopState('nonexistent')).toBeUndefined()
      expect(currentState.activeLoops.size).toBe(0)
    })
  })

  describe('Loop status updates', () => {
    it('should update loop status', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)

      store.updateLoopStatus('card-1', 'paused')

      const updatedState = store.getLoopState('card-1')
      expect(updatedState?.status).toBe('paused')
    })

    it('should not update non-existent loop status', () => {
      const store = useLoopStore.getState()

      store.updateLoopStatus('nonexistent', 'paused')

      expect(store.getLoopState('nonexistent')).toBeUndefined()
    })
  })

  describe('Bulk operations', () => {
    it('should set all loops from array', () => {
      const store = useLoopStore.getState()
      const loopData = [
        { cardId: 'card-1', state: mockLoopState },
        { cardId: 'card-2', state: mockLoopState2 },
      ]

      store.setAllLoops(loopData)

      // Must get fresh state after mutations
      const currentState = useLoopStore.getState()
      expect(currentState.activeLoops.size).toBe(2)
      expect(currentState.getLoopState('card-1')).toEqual(mockLoopState)
      expect(currentState.getLoopState('card-2')).toEqual(mockLoopState2)
    })

    it('should clear all loops', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)
      store.setLoopState('card-2', mockLoopState2)

      // Must get fresh state after mutations
      let currentState = useLoopStore.getState()
      expect(currentState.activeLoops.size).toBe(2)

      currentState.clearAllLoops()

      // Get fresh state again after clearAllLoops
      currentState = useLoopStore.getState()
      expect(currentState.activeLoops.size).toBe(0)
      expect(currentState.getLoopState('card-1')).toBeUndefined()
      expect(currentState.getLoopState('card-2')).toBeUndefined()
    })
  })

  describe('Analytics and selectors', () => {
    beforeEach(() => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState) // running
      store.setLoopState('card-2', mockLoopState2) // paused
      store.setLoopState('card-3', mockLoopState3) // completed
    })

    it('should get active loop count (running + paused)', () => {
      const store = useLoopStore.getState()
      const activeCount = store.getActiveLoopCount()

      expect(activeCount).toBe(2) // running + paused, not completed
    })

    it('should get only running loops', () => {
      const store = useLoopStore.getState()
      const runningLoops = store.getRunningLoops()

      expect(runningLoops).toHaveLength(1)
      expect(runningLoops[0]).toEqual(mockLoopState)
    })

    it('should calculate total cost', () => {
      const store = useLoopStore.getState()
      const totalCost = store.getTotalCost()

      // 2.50 + 1.25 + 5.75 = 9.50
      expect(totalCost).toBe(9.50)
    })

    it('should handle empty loops for analytics', () => {
      const store = useLoopStore.getState()
      store.clearAllLoops()

      // Must get fresh state after mutations
      const currentState = useLoopStore.getState()
      expect(currentState.getActiveLoopCount()).toBe(0)
      expect(currentState.getRunningLoops()).toHaveLength(0)
      expect(currentState.getTotalCost()).toBe(0)
    })
  })

  describe('Edge cases', () => {
    it('should handle updating iteration for removed loop', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)
      store.setLoopState('card-1', null) // Remove it

      store.updateLoopIteration('card-1', 10, 5.00, 25000)

      // Must get fresh state after mutations
      const currentState = useLoopStore.getState()
      expect(currentState.getLoopState('card-1')).toBeUndefined()
    })

    it('should handle updating status for removed loop', () => {
      const store = useLoopStore.getState()
      store.setLoopState('card-1', mockLoopState)
      store.setLoopState('card-1', null) // Remove it

      store.updateLoopStatus('card-1', 'completed')

      // Must get fresh state after mutations
      const currentState = useLoopStore.getState()
      expect(currentState.getLoopState('card-1')).toBeUndefined()
    })
  })
})
